-- Table structure for table `users`
--

CREATE TABLE `users` (
  `id` int NOT NULL,
  `name` varchar(399) COLLATE utf8mb4_unicode_ci NOT NULL,
  `email` varchar(399) COLLATE utf8mb4_unicode_ci NOT NULL,
  `password` varchar(399) COLLATE utf8mb4_unicode_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
